function pageprocessorHome4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  $rootScope.appDisplayName = "Posts";
  $rootScope.pushUrl = "https://push.kryptosmobile.com";
  //$rootScope.pushUrl = "http://172.20.4.74:8080";
  //MyCampusApp.rootScope.tenant = "ADU";
  $scope.reachedLastPost = false;
  $scope.showLoadingBar = false;

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.lastPostObjectID = "";
  try {
    if ($.jStorage.get("deviceID") == null || $.jStorage.get("deviceID") == undefined) {

      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      MyCampusApp.rootScope.push = PushNotification.init({
        android: {
          senderID: "272350014580"
        },
        browser: {
          pushServiceURL: 'http://push.api.phonegap.com/v1/push'
        },
        ios: {
          alert: "true",
          badge: "true",
          sound: "true"
        },
        windows: {}
      });

      MyCampusApp.rootScope.push.on('registration', function(data) {
        var devicePushID = data.registrationId;
        //alert(devicePushID);
        var pushDeviceData = {
          "tenant": MyCampusApp.rootScope.tenant,
          "id": devicePushID,
          "type": device.platform,
          "channel": "all"
        };

        $http.post($rootScope.pushUrl + "/kryptosds/push/adddeviceToChannel", pushDeviceData).success(function(response) {
          $.unblockUI();
          $.jStorage.set("deviceID", devicePushID);
          //$location.path("/app/Home4/Home4");
          //alert(JSON.stringify(response));
        }).
        error(function(err) {
          $.unblockUI();
          alert("err" + JSON.stringify(response));
        });

      });
    }
  } catch (e) {
    $.unblockUI();
  }

  $scope.showNewPostsBtn = false;

  var data2 = '<div style="background:#fff;background-color:#fff;margin-top:-20px;z-index:1000;"><div class="form-group"><div class="col-sm-6 col-xs-6" style="text-align:center;border-right:1px solid #efefef;border-bottom:1px solid #bcbcbc;padding:8px 0;color:#232323;" ng-click="createPost();"><img src="data:image/png;base64," style="width:22px;" />&nbsp;&nbsp;Create Post</div><div class="col-sm-6 col-xs-6" style="text-align:center;border-bottom:1px solid #bcbcbc;padding:8px 0;color:#232323;" ng-click="showCategories();"><img src="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADAAAAAwAQMAAABtzGvEAAAABlBMVEUAAAAjIyODokdlAAAAAXRSTlMAQObYZgAAACBJREFUGNNjwAf+M/wHAhh1mOEAAwMzVgpNJR4w3O0DAEU/Tg2sIyCsAAAAAElFTkSuQmCC" style="width:22px;" />&nbsp;&nbsp;Categories</div><div style="clear:both"></div></div></div>';

  data2 += '<div id="newPostBtn" ng-show="showNewPostsBtn" style="text-align:center;margin-top:-10px;z-index:-1;"><div class="btn btn-primary" ng-click="LoadPosts()">New Posts</div></div>';
  var htmlView = $compile($(data2))($scope);

  $("#fixedHeaderContent").html(htmlView);
  /*MyCampusApp.rootScope.push.on('notification', function(data) {
    switch (data.additionalData.openApplet) {
      case 'PushApp':
        {
          window.location.href = "index.html#app/Home4/Home4";
          break;
        }
    }
  });
*/

  var Feeddata;
  var FeedUrl;
  if ($rootScope.channelID == 'all' || $rootScope.channelID == undefined) {
    //if ($rootScope.channelID == undefined) {
    Feeddata = {
      "tenant": MyCampusApp.rootScope.tenant
    };
    FeedUrl = $rootScope.pushUrl + "/kryptosds/push/getTenantFeed";
  } else {
    Feeddata = {
      "tenant": MyCampusApp.rootScope.tenant,
      "channelId": $rootScope.channelID
    };

    FeedUrl = $rootScope.pushUrl + "/kryptosds/push/getTenantChannelFeed";
  }

  //alert(JSON.stringify(data));
  $scope.LoadPosts = function() {

    $scope.showNewPostsBtn = false;

    $.blockUI({
      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });

    var url = $rootScope.pushUrl + "/kryptosds/push/getChannels";

    var data = {
      "tenant": MyCampusApp.rootScope.tenant
    };

    $http.post(url, data).success(function(response) {
      //alert(JSON.stringify(response));
      if (response.error) {

      } else {
        $scope.channels = response;
        var userInfo = $.jStorage.get("userdetails");
        $scope.loggedInUserMail = userInfo.email;


        $http.post($rootScope.pushUrl + "/kryptosds/push/getUserLikedPosts", {
          'tenant': MyCampusApp.rootScope.tenant,
          'email': userInfo.email
        }).success(function(response) {
          if (response.length > 0) {
            $scope.userLikedPost = response[0].posts;
          } else {
            $scope.userLikedPost = [];
          }
          Feeddata.postObjectID = "";
          $http.post(FeedUrl, Feeddata).success(function(response) {

            if (response.error) {
              navigator.notification.alert("No post found. Start a new post.", null, 'Post', 'Ok');
              //$rootScope.channelSelected = $rootScope.channelID;
              //$location.path("/app/Home4/Home4Page3");
            } else {
              $scope.pushFeed = [];
              $scope.pushFeed = response.posts;
              $(".app-content .scrollable-content").animate({
                scrollTop: 0
              }, 800);
              $scope.PushCounts = response.postCounts;

              $scope.lastPostObjectID = response.posts[response.posts.length - 1]._id;
              //alert($scope.lastPostObjectID);
              if (response.posts.length == 5) {
                $scope.showLoadingBar = true;
              } else {
                $scope.showLoadingBar = false;
              }
            }
            $.unblockUI();
          }).
          error(function(err) {
            $.unblockUI();
            alert("err" + JSON.stringify(response));
          });
        }).error(function(err) {
          $.unblockUI();
          alert("err" + JSON.stringify(response));
        })

      }
    }).
    error(function(err) {
      $.unblockUI();
      alert("err" + JSON.stringify(response));
    });

  }

  $scope.calculateTimeDiff = function(d1) {

    var d2 = new Date();
    d1 = new Date(d1);
    var diff = d2 - d1;
    var seconds = diff / 1000;
    var numyears = Math.floor(seconds / 31536000);
    var numdays = Math.floor((seconds % 31536000) / 86400);
    var numhours = Math.floor(((seconds % 31536000) % 86400) / 3600);
    var numminutes = Math.floor((((seconds % 31536000) % 86400) % 3600) / 60);
    var numseconds = (((seconds % 31536000) % 86400) % 3600) % 60;
    if (numyears == -1) {
      return "Just Now";
    } else if (numyears != 0) {
      return (numyears == 0 ? '' : (numyears == 1 ? numyears + ' yr ' : numyears + " yrs ")) + " ago";
    } else if (numdays != 0) {
      return (numdays == 0 ? '' : (numdays == 1 ? numdays + ' day ' : numdays + " days ")) + " ago";
    } else if (numhours != 0) {
      return (numhours == 0 ? '' : (numhours == 1 ? numhours + ' hr ' : numhours + " hrs ")) + " ago";
    } else if (numminutes != 0) {
      return (numminutes == 0 ? '' : (numminutes == 1 ? numminutes + ' min ' : numminutes + " mins ")) + " ago";
    } else if (numseconds != 0) {
      return (numseconds == 0 ? '' : (numseconds == 1 ? numseconds + ' sec ' : parseInt(numseconds) + " secs ")) + " ago";
    }
  }

  $scope.createPost = function() {
    $location.path("/app/Home4/Home4Page3");
  }
  $scope.showCategories = function() {
    $location.path("/app/Home4/Home4Page2");
  }

  $scope.openPost = function(id) {
    $rootScope.postInfo = $scope.pushFeed[id];
    $location.path("/app/Home4/Home4Page4");
  }

  $scope.getChannel = function(id) {
    if (id == "all") {
      return 'Posted in: All';
    } else {
      for (var i = 0; i < $scope.channels.length; i++) {

        if ($scope.channels[i].channelId == id) {
          return 'Posted in: ' + $scope.channels[i].channel;
          break;
        }
      }
    }
  }

  $scope.showLikes = function(id) {
    var postId = $scope.pushFeed[id].postId;
    var data = {
      "tenant": MyCampusApp.rootScope.tenant,
      "channelId": $scope.pushFeed[id].channelId,
      "postId": postId
