        data: proxyData,
        success: function(data) {
          $rootScope.appDisplayName = placeType;
          //$(".placeHeader").html(placeType + "S");
          //displayPlaces(data)
          //alert ("Data : " + JSON.stringify(data));
          console.log(JSON.stringify(data));
          $rootScope.placesdata = data;
          $rootScope.$apply(function() {
            $location.search("placetype", placeType).path("/app/Places2/Places2Page2");
            $.unblockUI();
          });
        },
        error: function(e) {
          $.unblockUI();
          //alertServerError(e.status);
          alert("Error : " + e);
        }

      });

    } catch (e) {
      alert(e)
    }

  }

  var geocoder;
  var placeCoordinate;
  var errorCallback = function(error) {
    $.unblockUI();
    switch (error.code) {
      case 0:
        {
          navigator.notification.alert("Unable to locate your location.", function() {}, 'Alert', 'Ok');
          break;
        }
      case 1:
        {
          navigator.notification.alert("Your device GPS is not enabled. Please enable it to get direction to locations.", function() {}, 'Alert', 'Ok');
          break;
        }
      case 2:
        {
          navigator.notification.alert("Oops! Your Position is not available.", function() {}, 'Alert', 'Ok');
          break;
        }
    }
  }
  var placeGeoCoder = function(results) {
    try {
      $.unblockUI();
      

      //alert("place geo coder : " + JSON.stringify(results));
      console.log(JSON.stringify(results));
      placeCoordinate = results;
      $rootScope.placeCoordinate = placeCoordinate;
      geocoder = new google.maps.Geocoder();
      var latlng = new google.maps.LatLng(results.coords.latitude, results.coords.longitude);
      geocoder.geocode({
        'latLng': latlng
      }, function(results, status) {
        //alert("Result1 : " + JSON.stringify(results));
        //console.log(JSON.stringify(results));
        if (status == google.maps.GeocoderStatus.OK) {
          if (results[0]) {
            //alert("Result : " + JSON.stringify(results[0]));
            //alert("formatted result : " + results[0].formatted_address);
            console.log(JSON.stringify(results[0]));
            $("#currentUserLoc").html(results[0].formatted_address);
          } else {
            alert('No results found');
          }
        } else {
          alert('Geocoder failed due to: ' + status);
        }
      });
    } catch (e) {
      $.unblockUI();
      alert("Error : " + e);
    }
    //pageChange("places");
  }

  //if (window.device) {
  var placescb = function() {
    if (navigator.geolocation) {
      $.blockUI({
    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
  });
      navigator.geolocation.getCurrentPosition(placeGeoCoder, errorCallback, {
        timeout: 10000,
        frequency: 3000,
        enableHighAccuracy: true
      });
    } else {
      alert("not working")
    }
  }
  window.placescb = placescb;
  $.getScript("https://maps.googleapis.com/maps/api/js?key=" + mapapikey + "&sensor=false&callback=placescb");
  //}
}

